import React from "react";
import { gql, useQuery } from '@apollo/client';
import { Navigate, useLocation } from 'react-router-dom';

const GET_PROJECT = gql`
    query getProject($name: String!) {
        getProject(name: $name) {
            id
            name
            url
            description
        }
    }
`;


const Query = () => {
    // const [modal, setModal] = useState(false)
    const {state} = useLocation();
    const {name} = state;

    const { loading, error, data } = useQuery(GET_PROJECT, {
        variables: { name }
    });

    if (loading) return 'Loading...';
    if (error || data.getProject === null ) {
        return(
            <Navigate to={{ pathname: "/404"}}/>
        )
    };

    return (
        <div style={{ backgroundColor:'#fefefe', padding:'150px 0', display:'flex', gap:'60px', justifyContent:'center', flexDirection:'column', alignItems:'center'}} > 
            <span style={{ fontSize:'36px', fontFamily:'Quicksand', fontWeight:'400', color:'teal'}}>Project Name: {data.getProject.name}</span>
            <div style={{ display:'inline-block', margin:'0 25%', textAlign:'center',fontSize:'18px', fontFamily:'Quicksand', fontWeight:'400' }}>{data.getProject.description}</div>
            <p>If you cannot view the frame below</p>
            <iframe src={data.getProject.url} height={800} width={1500} style={{ border:'none' }}/>
        </div>
    )
}

export default Query;
