import { Query } from '@apollo/react-components';
import {gql} from "apollo-boost"

const GET_PROJECT = gql`
{
  getProject(name: "Boolario") {
    id
    name
    url
    }
}
`

const project = () => {
    <Query
    query={GET_PROJECT}
    >
    {({ loading, error, data }) => {
      if (loading) return <p>Loading...</p>;
      if (error) return <p>Error :(</p>;

      return data.getProject.map(({ id, name, url }) => (
        <div key={id}>
          <p>
            {id}: {name}: {url}
          </p>
        </div>
      ));
    }}
  </Query>
}
