import React, { useState } from "react";
import { useNavigate } from 'react-router-dom';
import Background from '../media/callout-bg.png'



const Project = () => {

    const [message, setMessage] = useState('');
    const navigate = useNavigate()

    const handleChange = event => {
        setMessage(event.target.value);
    };

    const handleClick = () => {
        return navigate(`/project/:${message}`, {state: { name: message }})
    }
    
    return (
        <div style={{backgroundImage: `url(${Background})`, backgroundSize:'cover', height: "100vh" }}> 
            <div style={{ display:'block', padding:'50% 50%' }}>
                <form style={{ display:'flex', flexDirection:'column' }}>
                    <label>Project Name:</label>
                    <input id="search" type="text" name="search" value={message} onChange={handleChange} placeholder='Search...'/>
                    <button onClick={handleClick}>Submit</button>
                </form>
            </div>
        </div>
    )
}

export default Project;
