import React, { useRef, useState } from "react";
import { useNavigate } from 'react-router-dom';
import ReactDOM from 'react-dom/client';
import Searchbar from '../components/Searchbar'
import Query from '../routes/Query'



const Project = () => {

    // const onSubmit = input => {
    //     setModal(false)
    // }

    // if (modal) {
    //     return <NewPetModal onSubmit={onSubmit} onCancel={() => setModal(false)} />
    // }
    const ref = useRef(null)
    const [message, setMessage] = useState('');
    const navigate = useNavigate()

    const handleChange = event => {
        setMessage(event.target.value);
    };

    const handleClick = () => {
        return navigate(`/projects/${message}`)
    }
    
    return (
        <div> 
            <form>
                <label>Project Name:
                <input id="search" type="text" name="search" value={message} onChange={handleChange} placeholder='Search...'/>
                </label>
                <button ref={ref}  onSubmit={handleClick}>Submit</button>
            </form>
            {/* <button onClick={() => setModal(true)}>new project</button> */}
            {/* {this.state.sh} */}
        </div>
    )
}

export default Project;
