import React, { useRef, useState } from "react";
import { Navigate } from 'react-router-dom';
import ReactDOM from 'react-dom/client';
import Searchbar from '../components/Searchbar'
import Query from '../routes/Query'



const Project = () => {

    // const onSubmit = input => {
    //     setModal(false)
    // }

    // if (modal) {
    //     return <NewPetModal onSubmit={onSubmit} onCancel={() => setModal(false)} />
    // }
    const ref = useRef(null)
    const handleClick = () => {
        <Navigate to={{ pathname: `/projects?${ref.current}`}}/>
    }
    
    return (
        <div> 
            <form>
                <label>Project Name:
                <input id="search" type="text" name="search" value={message} placeholder='Search...'/>
                </label>
                <button ref={ref} type="submit" onClick={handleClick}>Submit</button>
            </form>
            {/* <button onClick={() => setModal(true)}>new project</button> */}
        </div>
    )
}

export default Project;
